            if (std.mem.eql(u8, u_name, "u_alpha_cutoff")) {
                var desc: sg.ShaderUniformDesc = .{};
                desc.name = "u_alpha_cutoff";
                desc.type = .FLOAT;
                desc.array_count = 0;
                return desc;
            }
        }
    }
    return null;
